    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        
        String s1 = jTextField1.getText();
        int num = Integer.parseInt(s1);
        
        String menu = (String)jComboBox1.getSelectedItem();
        
        if(menu.equals("Dollars"))
        {
            double dollar = 65;
            double rupee = num / dollar;
            
            String s2 = Double.toString(rupee);
            jTextField2.setText(s2);
        }
        else if(menu.equals("Pound"))
        {
            double pound = 76;
            double rupee = num / pound;
            
            String s2 = Double.toString(rupee);
            jTextField2.setText(s2);
        }
        else if(menu.equals("Euro"))
        {
            double euro = 86;
            double rupee = num/euro;
            
            String s2 = Double.toString(rupee);
            jTextField2.setText(s2);
        }
        else
        {
            double franc = 69;
            double rupee = num/franc;
            
            String s2 = Double.toString(rupee);
            jTextField2.setText(s2);
        }
        // TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

