    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        
        String menu = (String)jComboBox1.getSelectedItem();
        
        if(menu.equals("Circle"))
        {
            this.dispose();
            Circle o = new Circle();
            o.setVisible(true);
            o.setSize(440,380);
            o.setLocation(600,300);
        }
        else if(menu.equals("Triangle"))
        {
            this.dispose();
            Triangle o = new Triangle();
            o.setVisible(true);
            o.setSize(440,380);
            o.setLocation(600,300);
        }
        else if(menu.equals("Rectangle"))
        {
           this.dispose();
           Rec r = new Rec();
           r.setVisible(true);
           r.setSize(440,380);
           r.setLocation(600,300);
           
        }
        else
        {
            this.dispose();
            Square o = new Square();
            o.setVisible(true);
            o.setSize(440,380);
            o.setLocation(600,300);
        }
        // TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

